mapper.update:

update product 
set product_code = #{product_code}, product_name = #{product_name}, 
	product_price = #{product_price}, product_brand = #{product_brand}, 
	product_option = #{product_option}, product_explain = #{product_explain}, 
	product_image = #{product_image}, product_type = #{product_type}
where product_code = #{product_code}
